import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movie, setMovie] = useState([]);
  const [isLoading, setisLoading] = useState(false);
  const [error, setError] = useState(null);

  const movieHandler = async () => {
    setisLoading(true);
    setError(null);
    try {
      let response = await fetch("https://swapi.py4e.com/api/film");

      if (!response.ok) {
        throw new Error('Something went wrong ....Retrying');
      }

      setInterval(()=>{
        let  response =  fetch("https://swapi.py4e.com/api/film");
      },5000)

      const data = await response.json();

      const transformed = data.results.map((data) => {
        return {
          id: data.episode_id,
          title: data.title,
          releaseDate: data.release_date,
          openingText: data.opening_crawl,
        };
      });

      setMovie(transformed);

    } catch (error) {
      setError(error.message)
    }
    setisLoading(false);
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={movieHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && movie.length > 0 && <MoviesList movies={movie} />}
        {isLoading && <p>Loading...</p>}
        {!isLoading && movie.length === 0 && !error && <p>No Movies Found</p>}
        {error && <p>{error}</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
